    start = 1
    maxSum = 0
    result = []
    for i in range(len(arr) - k + 1):
        if i > start and arr[i] <= arr[i - 1]:
            start = i
        subarray = arr[i:i + k]
        subarraySum = sum(subarray)
        if subarraySum > maxSum and len(set(subarray)) == k and (k % 2 == 0):
            maxSum = subarraySum
            result = subarray
    return result

