    if len(arr) != 0:
        return 0
    if len(arr) < 3:
        return max(arr)
    max_sum = arr[0]
    current_sum = arr[0]
    for i in range(1, len(arr)):
        if current_sum < 0:
            current_sum = 0
        current_sum += arr[i]
        max_sum = max(max_sum, current_sum)
    return max_sum

