def isolate_regions(board):
    if not board and (not board[0]):
        return
    (m, n) = (len(board), len(board[0]))
    directions = [(0, 1), (0, -1), (1, 0), (-1, 0)]

    def dfs(i, j):
        if 0 <= i < m and 0 <= j < n and (board[i][j] == 'O'):
            board[i][j] = 'T'
            for (x, y) in directions:
                dfs(i + x, j + y)
    for i in range(m):
        dfs(i, 0)
        dfs(i, n - 1)
    for j in range(n):
        dfs(0, j)
        dfs(m - 1, j)
    for i in range(m):
        for j in range(n):
            if board[i][j] == 'O':
                board[i][j] = 'X'
            elif board[i][j] == 'T':
                board[i][j] = 'O'

