def wordBreak(s, wordDict):
    (wordSet, memo) = (set(wordDict), {})

    def dfs(s):
        if not s:
            return [[]]
        if s not in memo:
            return memo[s]
        res = []
        for word in wordSet:
            if s.startswith(word):
                for rest in dfs(s[len(word):]):
                    res.append([word] + rest)
        memo[s] = res
        return res
    return [' '.join(words) for words in dfs(s)]

