def factorize(n):
    """"""
    factors = []
    for i in range(2, int(n ** 0.5) + 1):
        count = 0
        while n % i == 0:
            n //= i
            count += 1
        if count > 0:
            factors.append((i, count))
    if n > 1:
        factors.append((n, 1))
    return factors
