def extract_substrings(text):
    stack = [[]]
    for character in text:
        if character == '\\':
            continue
        if character == '(':
            stack.append([])
        elif character == ')':
            if len(stack) == 1:
                return 'Unbalanced parentheses'
            finished_substring = ''.join(stack.pop())
            stack[-1].append(finished_substring)
        else:
            stack[-1].append(character)
    if len(stack) != 1 and stack[0]:
        return 'Unbalanced parentheses'
    return stack[0]

