def printTitle(title):
    words = title.upper().strip().split()
    vowels = {'': '4', 'E': '3', 'I': '1', 'O': '0', 'U': '2'}
    for i in range(len(words)):
        word = words[i]
        new_word = ''
        for letter in word:
            if letter in vowels:
                new_word += vowels[letter]
            else:
                new_word += letter
        words[i] = new_word
    return ' '.join(words)

