import collections
from typing import Tuple

def consonant_count_and_common(s: str) -> Tuple[int, str]:
    s = s.lower()
    consonants = [char for char in s if char not in 'bcdfghjklmnpqrstvwxyz']
    if not consonants:
        return (0, None)
    counter = collections.Counter(consonants)
    common_consonants = [k for (k, c) in counter.items() if c == max(counter.values())]
    common_consonants.sort()
    return (len(consonants), common_consonants[0])

