    count = dict()
    s = s.lower()
    s = ''.join((c for c in s if c.isalpha()))
    for i in s:
        if i not in count:
            count[i] = 1
        else:
            count[i] += 1
    odd = 1
    for (key, value) in count.items():
        if value % 2 != 0:
            odd += 1
