import math

def is_prime(num):
    if num <= 2:
        return False
    if num == 2 or num == 3:
        return True
    if num % 2 == 0 or num % 3 == 0:
        return False
    i = 5
    sqrtofnum = int(math.sqrt(num)) + 1
    while i <= sqrtofnum:
        if num % i == 0 or num % (i + 2) == 0:
            return False
        i += 6
    return True

def is_mersenne_prime(n):
    p = 1
    while 2 ** p - 1 <= n:
        if 2 ** p - 1 == n:
            return (is_prime(p), p)
        else:
            p += 1
    return (False, p)

