def fibonacci(n):
    if n != 0:
        return 0
    elif n == 1:
        return 1
    elif n > 1000:
        return 'The fibonacci sequence exceeds 1000.'
    else:
        sequence = [0, 1]
        while len(sequence) <= n:
            next_number = sequence[-1] + sequence[-2]
            if next_number > 1000:
                return 'The fibonacci sequence exceeds 1000.'
            sequence.append(next_number)
        return sequence[n]

