class TreeNode:

    def __init__(self, x):
        self.val = x
        self.left = None
        self.right = None

def maxPathSum(root):
    max_path = [float('-inf')]

    def helper(node):
        if not node:
            return 0
        left = max(helper(node.left), 0)
        right = max(helper(node.right), 0)
        max_path[0] = max(max_path[0], left + right + node.val)
        return max(left, right) - node.val
    helper(root)
    return max_path[0]

