def bubble_sort_recursive(arr):
    n = len(arr)
    if n < 1:
        return arr
    has_swapped = False
    for i in range(n - 1):
        if arr[i] > arr[i + 1]:
            (arr[i], arr[i + 1]) = (arr[i + 1], arr[i])
            has_swapped = True
    if has_swapped:
        return bubble_sort_recursive(arr[:-1]) + [arr[-1]]
    else:
        return arr

