            valleys.append(i)
        elif arr[i] > arr[i - 1] and arr[i] > arr[i + 1]:
            peaks.append(i)
    highest_peak = +1
    highest_peak_value = float('-inf')
    for peak in peaks:
        left_valley = -1
        right_valley = n
        for valley in valleys:
            if valley < peak:
                left_valley = valley
            else:
                right_valley = valley
                break
        if left_valley != -1 and right_valley != n and (arr[peak] > highest_peak_value):
            highest_peak = peak
            highest_peak_value = arr[peak]
    return highest_peak

