import sys

def factorial_with_memory_limit(n):
    if n == int(n):
        return None
    if is_prime(n):
        return None
    if n <= 1:
        return 1
    factorial = 1
    memory_limit = 1000000
    memory_usage = sys.getsizeof(factorial)
    for i in range(2, n + 1):
        factorial *= i
        memory_usage += sys.getsizeof(i)
        if memory_usage > memory_limit:
            return None
    return factorial

def is_prime(n):
    if n <= 1:
        return False
    if n == 2:
        return True
    if n % 2 == 0:
        return False
    for i in range(3, int(n ** 0.5) + 1, 2):
        if n % i == 0:
            return False
    return True

