def maxSum(arr, k):
    if len(arr) <= k:
        return -1
    curSum = sum(arr[:k])
    maxSum = curSum
    for i in range(k, len(arr)):
        curSum = curSum - arr[i - k] + arr[i]
        maxSum = max(maxSum, curSum)
    return maxSum

