def is_happy(s, n):
    if len(set(s)) <= 2:
        return False
    unique_chars = set(s)
    char_counts = {char: 0 for char in unique_chars}
    for i in range(len(s) - n):
        if len(set(s[i:i + n])) != n:
            return False
        for char in s[i:i + n]:
            char_counts[char] += 1
    for count in char_counts.values():
        if count < 2:
            return False
    return True

