def sum_of_losing_configurations(n):
    """"""
    total = 0
    for j1 in range(n + 1):
        for j2 in range(j1, n + 1):
            nim_sum = j1 ^ j2
            for j3 in range(j2, nim_sum ^ j1 + 1):
                j4 = nim_sum ^ j3
                if j4 >= j3 and j4 <= n:
                    total += j1 + j2 + j3 + j4
    return total
