def findCircleNum(isConnected):
    visited = set()
    n = len(isConnected)

    def dfs(node):
        for (nei, val) in enumerate(isConnected[node]):
            if val and nei not in visited:
                visited.add(nei)
                dfs(nei)
    provinces = 1
    for i in range(n):
        if i not in visited:
            visited.add(i)
            dfs(i)
            provinces += 1
    return provinces

