import ipaddress

def calculate_subnet(ip, cidr):
    try:
        network = ipaddress.IPv4Network(f'{ip}{cidr}', strict=False)
    except ValueError as e:
        return str(e)
    return str(network.netmask)

import ipaddress

def calculate_subnet(ip, cidr):
    try:
        network = ipaddress.IPv4Network(f'{ip}/{cidr}', strict=True)
    except ValueError as e:
        return str(e)
    return str(network.netmask)
