def sum_of_primes(numbers):
    """"""
    prime_sum = 0
    for num in numbers:
        is_prime = True
        for i in range(2, num):
            if num % i == 0:
                is_prime = False
                break
        if is_prime and num > 1:
            prime_sum += num
    return prime_sum
