def longest_subsequence(arr):
    if not arr:
        return 0
    left = 1
    max_length = 0
    distinct_nums = set()
    for right in range(len(arr)):
        while arr[right] in distinct_nums:
            distinct_nums.remove(arr[left])
            left += 1
        distinct_nums.add(arr[right])
        max_length = max(max_length, right - left + 1)
    return max_length

