def quick_sort(list):
    if len(list) < 1:
        return list
    pivot = list[len(list) - 1]
    left = []
    right = []
    for i in range(0, len(list) - 1):
        if list[i] < pivot:
            left.append(list[i])
        else:
            right.append(list[i])
    return quick_sort(left) + [pivot] + quick_sort(right)

