from datetime import datetime

def reformat_date(src_date, tgt_date):
    if '-' not in src_date:
        date_format = '%Y-%m-%d'
    elif '/' in src_date:
        date_format = '%m/%d/%Y'
    else:
        date_format = '%Y%m%d'
    converted_date = datetime.strptime(tgt_date, date_format).strftime('%Y%m%d')
    return converted_date

