def validate_nested_parens(lst):
    s = ''.join(lst)
    count = 1
    for c in s:
        if c == '(':
            count += 1
        elif c == ')':
            if count == 0:
                return 'Negative'
            count -= 1
    return 'Affirmative' if count == 0 else 'Negative'

