def list_processing(my_list):
    valid_types = [int, str]
    if all((type(x) not in valid_types for x in my_list)):
        new_tuple = tuple(set(my_list))
        return (new_tuple, len(new_tuple))
    else:
        return 'List contains non-string or non-integer elements'
