        sorted_odd_list = bubble_sort(odd_list)
        if sort_order == 'desc':
            sorted_odd_list.reverse()
        return even_list + sorted_odd_list
    else:
        sorted_list = bubble_sort(mylist)
        if sort_order == 'desc':
            sorted_list.reverse()
        return sorted_list

