from typing import List, Union

def below_zero(operations: List[Union[int, float]], handle_float: bool=True, balance_limit: int=None) -> bool:
    balance = 0
    if handle_float:
        operations = [round(op, 2) for op in operations]
    for op in operations:
        if op < 0:
            print('Negative deposit operation is invalid!')
            continue
        if balance_limit and op > balance_limit:
            print('The withdrawal operation exceeds the balance limit!')
            continue
        balance += op
        if balance < 0:
            return True
    return False

