def sum_of_cubes_of_prime_numbers(n):

    def is_prime(num):
        if num < 2:
            return False
        for i in range(2, int(num ** 0.5) + 1):
            if num % i == 0:
                return False
        return True
    if n < 0:
        return 'n must be a positive integer.'
    primes = []
    num = 2
    while len(primes) < n:
        if is_prime(num):
            primes.append(num)
        num += 1
    return sum((prime ** 3 for prime in primes))

