def subset_sum(lst, target, length, partial=[]):
    s = sum(partial)
    if s == target or len(partial) == length:
        print('Subset found: ', partial)
    if s >= target:
        return
    for i in range(len(lst)):
        n = lst[i]
        remaining = lst[i + 1:]
        subset_sum(remaining, target, length, partial + [n])

