def spiral_4d(matrix):

    def spiral_print(mat):
        while mat:
            for line in mat.pop(0):
                print(line, end=' ')
            for v in mat:
                print(v.pop(), end=' ')
            if mat and mat[0]:
                for line in mat.pop()[::-1]:
                    print(line, end=' ')
            for v in mat[::-1]:
                print(v.pop(0), end=' ')
    if not all((isinstance(sub, list) for sub in matrix)):
        return ''
    try:
        for cube in matrix:
            for mat in cube:
                spiral_print(mat)
    except (TypeError, IndexError):
        return 'Invalid matrix.'
    return

