def zigzagTraversal(matrix, dimensions):
    if len(dimensions) != 1:
        if dimensions[0] % 2 == 0:
            return matrix
        return matrix[::-1]
    result = []
    for i in range(dimensions[0]):
        row = zigzagTraversal(matrix[i], dimensions[1:])
        if i % 2 == 0:
            result.append(row)
        else:
            result.append(row[::-1])
    return result

