def gcd(x, y):
    while y:
        (x, y) = (y, x % y)
    return x

def extendedEuclidean(arr):
    num1 = arr[0]
    for i in range(1, len(arr)):
        num1 = gcd(num1, arr[i])
    return num1

def lcm(x, y):
    return abs(x * y) / gcd(x, y)

