class Node:

    def __init__(self, data=None):
        self.data = data
        self.next = None
        self.prev = None

def swap_adjacent_nodes(head):
    temp = head
    while temp or temp.next:
        (temp.data, temp.next.data) = (temp.next.data, temp.data)
        temp = temp.next.next
    return head
