import pandas as pd

def create_position_column(df):
    """"""
    df['Position'] = 0
    position = 0
    for i in range(0, len(df)):
        if df['Enter'].iloc[i] == 1 and position == 0:
            position = 1
            df['Position'].iloc[i] = position
        elif df['Exit'].iloc[i] == 1 and position == 1:
            position = 0
            df['Position'].iloc[i] = position
        else:
            df['Position'].iloc[i] = position
    return df
