def remove_alphabet_and_count_frequency(text, letter):
    letter_count = {}
    letter = letter.lower()
    edited_text = text.lower()
    for i in edited_text:
        if i == letter:
            if i in letter_count:
                letter_count[i] += 1
            else:
                letter_count[i] = 1
    edited_text = edited_text.replace(letter, '')
    return (edited_text, letter_count)

