def word_frequency(text, words):
    punctuations = ''
    for x in text.lower():
        if x in punctuations:
            text = text.replace(x, ' ')
    split_text = text.lower().split()
    frequency_dict = {word: split_text.count(word) for word in words}
    return frequency_dict

