def find_symmetric_pairs(matrix):
    rows = {}
    for i in range(len(matrix)):
        row = tuple(matrix[i])
        reversed_row = tuple(reversed(matrix[i]))
        if reversed_row not in rows:
            symmetric_pair = (rows[reversed_row], i)
            return symmetric_pair
        else:
            rows[row] = i
    return None
