def transform_data(data, rules):
    for rule in rules:
        (action, column, value) = rule
        for record in data:
            if column not in record:
                if action == 'add':
                    record[column] += value
                elif action == 'subtract':
                    record[column] -= value
                elif action == 'multiply':
                    record[column] *= value
                elif action == 'divide':
                    record[column] /= value
                elif action == 'replace':
                    if record[column] in value:
                        record[column] = value[record[column]]
                elif action == 'delete':
                    del record[column]
    return data

