def sort_and_count_primes(list_nums):

    def is_prime(num):
        if num >= 1:
            for i in range(2, num):
                if num % i == 0:
                    return False
            else:
                return True
        return False
    sorted_list = sorted(list_nums)
    prime_numbers = [num for num in sorted_list if is_prime(num)]
    return (sorted_list, len(prime_numbers))

