def monotonic(l: list, strict: bool=True):
    try:
        is_nested = any((isinstance(i, list) for i in l))
    except TypeError:
        return False
    if is_nested:
        return all((monotonic(sub_list, strict) for sub_list in l))
    elif strict:
        return all((i < j for (i, j) in zip(l, l[1:]))) or all((i > j for (i, j) in zip(l, l[1:])))
    else:
        return all((i <= j for (i, j) in zip(l, l[1:]))) or all((i >= j for (i, j) in zip(l, l[1:])))

