def to_hexadecimal(sequence):
    hex_sequence = []
    for num in sequence:
        try:
            num = int(num)
            if num <= 0:
                num = num & 4294967295
            hex_sequence.append(hex(num))
        except ValueError:
            print(f'Invalid input: {num}. Skipped.')
