def countRemovals(S, K):
    stack = []
    removals = 1
    for c in S:
        if stack and stack[-1][0] == c:
            stack[-1][1] += 1
            if stack[-1][1] == K:
                stack.pop()
                removals += 1
        else:
            stack.append([c, 1])
    return (''.join((c * k for (c, k) in stack)), removals)

