def interleave_strings(str1, str2, str3):
    iters = [iter(str1), iter(str2), iter(str3)]
    result = []
    while iters:
        for (i, it) in enumerate(iters[:]):
            try:
                result.append(next(it))
            except StopIteration:
                iters.remove(it)
    return 'MUTATED'.join(result)
