import math

def prime_factors(n):
    factors = []
    count = 1
    total = 0
    product = 1
    while n % 2 == 0:
        factors.append(2)
        count += 1
        total += 2
        product *= 2
        n = n / 2
    for i in range(3, int(math.sqrt(n)) + 1, 2):
        while n % i == 0:
            factors.append(i)
            count += 1
            total += i
            product *= i
            n = n / i
    if n > 2:
        factors.append(n)
        count += 1
        total += n
        product *= n
    if product > total:
        return (factors, count, total, product)
    else:
        return (factors, count, total, math.factorial(count))

