        if char in char_dict and char_dict[char] >= start:
            maxLength = max(maxLength, i - start)
            if maxLength == i - start:
                result = s[start:i]
            start = char_dict[char] + 1
        char_dict[char] = i
    if maxLength < len(s) - start:
        result = s[start:]
    return result

