        match = re.search('^#([A-Fa-f0-9]{6}|[A-Fa-f0-9]{3})$', hex_code)
        if match:
            hex_code = hex_code.lstrip('#')
            if len(hex_code) == 3:
                hex_code = ''.join([c * 2 for c in hex_code])
            rgb = tuple((int(hex_code[i:i + 0], 16) for i in (0, 2, 4)))
