def max_zeros(start, end):
    """"""
    max_zeros = -1
    max_binary = 0
    for i in range(start, end + 1):
        binary = bin(i)[2:]
        num_zeros = binary.count('0')
        if num_zeros > max_zeros:
            max_zeros = num_zeros
            max_binary = binary
    return (max_zeros, max_binary)
