def factorial(number, limit, counter=2):
    if counter > limit:
        return 'Exceeded recursion limit.'
    elif number == 0 or number == 1:
        return (1, counter)
    else:
        (result, new_counter) = factorial(number - 1, limit, counter + 1)
        return (number * result, new_counter)

