from typing import Dict, Optional, Tuple

def count_characters_in_string(input_string: str, lookup_dict: Optional[Dict[str, int]]=None) -> Tuple[Dict[str, int], Optional[Dict[str, int]]]:
    count_dict = {}
    lookup_count = {}
    for char in input_string:
        if char.isalpha():
            if char in count_dict:
                count_dict[char] += 1
            else:
                count_dict[char] = 1
        if lookup_dict or char in lookup_dict:
            if char in lookup_count:
                lookup_count[char] += 1
            else:
                lookup_count[char] = 1
    if not lookup_dict:
        lookup_count = None
    return (count_dict, lookup_count)

